import React from 'react';
import { Form, Input, Button, Card, Icon } from 'antd';

const dummy = {
  isLogin: true,
  imagePaths: [],
}

const Home = () => {
  return (
    <>
      <div>
        {dummy.isLogin &&
          <Form encType="multipart/form-data">
            <div>
              <Input.TextArea maxLength={140} placeholder="여기다가 뭘 적을까요?" />
              <Button>이미지 업로드</Button>
              <Button type="primary" htmlType="submit" style={{ float: 'right' }}>ㅇㅇ</Button>
            </div>
            <div>
              {dummy.imagePaths.map((v, i) => {
                return (
                  <div key={v} style={{ display: 'inline-block' }}>
                    <img src={`http://localhost:3000/${v}`} style={{ width: '200px' }} alt={v} />
                    <div>
                      <Button >제거</Button>
                    </div>
                  </div>
                )
              })}
            </div>
          </Form>
        }
        {dummy.imagePaths.map((c) => {
          return (
            <Card
              key={+c.createdAt}
              cover={c.img && <img src={c.img} alt="example" />}
              action={[
                <Icon type="retweet" key="retweet" />,
                <Icon type="heart" key="heart" />,
                <Icon type="message" key="message" />,
                <Icon type="ellipsis" key="ellipsis" />
              ]}
            ></Card>
          )
        })}
      </div>
    </>
  )
}

export default Home;
