import React, { useState } from 'react';
import AppLayout from '../components/AppLayout';
import Head from 'next/head';
import { Form, Checkbox, Button, Input } from 'antd';

const SignUp = () => {
  const [ id, setId ] = useState('')
  const [ nickname, setNickname ] = useState('')
  const [ password, setPassword ] = useState('')
  const [ passwordCheck, setPasswordCheck ] = useState('')
  const [ term, setTerm ] = useState('')

  const onChangeId = () => {}

  const onChangeNickName = () => {}

  const onChangePassword = () => {}

  const onChangePasswordCheck = () => {}

  const onChangeTerm = () => {}

  const onSubmit = () => {}

  return(
    <>
      <Head>
        <title>Node-Bird</title>
        <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/4.4.0/antd.css" />
      </Head>
      <AppLayout>
        <Form onSubmit={onSubmit} style={{ padding: '10px' }}>
          <div>
            <label htmlFor="userId">아이디</label>
            <br />
            <Input name="userId" required onChange={onChangeId} />
          </div>
          <div>
            <label htmlFor="nickName">닉네임</label>
            <br />
            <Input name="nickName" required onChange={onChangeNickName} />
          </div>
          <div>
            <label htmlFor="password">비밀번호</label>
            <br />
            <Input name="password" type="password" required onChange={onChangePassword} />
          </div>
          <div>
            <label htmlFor="passwordCheck">비밀번호 확인</label>
            <br />
            <Input name="passwordCheck" type="password" required onChange={onChangePasswordCheck} />
          </div>
          <div>
            <Checkbox name="userTerm" value={term} onChange={onChangeTerm}>
              위 내용이 맞습니다.
            </Checkbox>
          </div>
          <div>
            <Button type="primary" htmlType="submit">가입하기</Button>
          </div>
        </Form>
      </AppLayout>
    </>
  )
}

export default SignUp;
