import React from 'react';
import { Form, Input, Button, Card, Avatar } from 'antd';
import { RetweetOutlined, HeartOutlined, MessageOutlined, EllipsisOutlined } from '@ant-design/icons';

const dummy = {
  isLogin: true,
  imagePaths: [],
  mainPosts: [{
    user: {
      id: 1,
      nickname: 'ykwimk',
    },
    content: 'asdfdasf'
  }],
}

const Home = () => {
  return (
    <>
      <div>
        {dummy.isLogin &&
          <Form encType="multipart/form-data" style={{ margin: '10px 0 20px' }}>
            <div>
              <Input.TextArea maxLength={140} placeholder="여기다가 뭘 적을까요?" />
              <Button>이미지 업로드</Button>
              <Button type="primary" htmlType="submit" style={{ float: 'right' }}>ㅇㅇ</Button>
            </div>
            <div>
              {dummy.imagePaths.map((v, i) => {
                return (
                  <div key={v} style={{ display: 'inline-block' }}>
                    <img src={`http://localhost:3000/${v}`} style={{ width: '200px' }} alt={v} />
                    <div>
                      <Button >제거</Button>
                    </div>
                  </div>
                )
              })}
            </div>
          </Form>
        }
        {dummy.mainPosts.map((c) => {
          return (
            <Card
              key={+c.createdAt}
              cover={c.img && <img src={c.img} alt="example" />}
              actions={[
                <RetweetOutlined />,
                <HeartOutlined />,
                <MessageOutlined />,
                <EllipsisOutlined />
              ]}
              extra={<Button>팔로우</Button>}
            >
              <Card.Meta
                avatar={<Avatar>{c.user.nickname[0]}</Avatar>}
                title={c.user.nickname}
                description={c.content}
              />
            </Card>
          )
        })}
      </div>
    </>
  )
}

export default Home;
